'use server'

import { cookies } from 'next/headers'
import axios from 'axios'
import { AuthResponse, User } from '@/types/user'

const STRAPI_URL = process.env.NEXT_PUBLIC_BACKEND_URL || 'http://localhost:1337'

export async function loginAction(prevState: any, formData: FormData) {
    const identifier = formData.get('email')
    const password = formData.get('password')

    if (!identifier || !password) {
        return { error: 'Please enter both email and password.' }
    }

    try {
        const { data } = await axios.post<AuthResponse>(`${STRAPI_URL}/api/auth/local`, {
            identifier,
            password,
        })

        const cookieStore = await cookies()
        cookieStore.set('jwt', data.jwt, {
            httpOnly: true,
            secure: process.env.NODE_ENV === 'production',
            maxAge: 60 * 60 * 24 * 7, // 1 week
            path: '/',
        })

        return { success: true, user: data.user }
    } catch (error: any) {
        console.error('Login error:', error.response?.data?.error || error.message)
        return {
            error: error.response?.data?.error?.message || 'Invalid credentials or server error.',
        }
    }
}

export async function registerAction(prevState: any, formData: FormData) {
    const username = formData.get('username')
    const email = formData.get('email')
    const password = formData.get('password')

    if (!username || !email || !password) {
        return { error: 'Please fill in all fields.' }
    }

    try {
        const { data } = await axios.post<AuthResponse>(`${STRAPI_URL}/api/auth/local/register`, {
            username,
            email,
            password,
        })

        const cookieStore = await cookies()
        cookieStore.set('jwt', data.jwt, {
            httpOnly: true,
            secure: process.env.NODE_ENV === 'production',
            maxAge: 60 * 60 * 24 * 7, // 1 week
            path: '/',
        })

        return { success: true, user: data.user }
    } catch (error: any) {
        console.error('Register error:', error.response?.data?.error || error.message)
        return {
            error: error.response?.data?.error?.message || 'Registration failed. Please try again.',
        }
    }
}

export async function logoutAction() {
    const cookieStore = await cookies()
    cookieStore.delete('jwt')
}

export async function getSession() {
    const cookieStore = await cookies()
    const jwt = cookieStore.get('jwt')?.value

    if (!jwt) return null

    try {
        const { data } = await axios.get<User>(`${STRAPI_URL}/api/users/me`, {
            headers: {
                Authorization: `Bearer ${jwt}`,
            },
        })
        return data
    } catch (error) {
        return null
    }
}

'use server'

import { cookies } from 'next/headers'
import axios from 'axios'

const STRAPI_URL = process.env.NEXT_PUBLIC_BACKEND_URL || 'http://localhost:1337'

export async function createCheckoutSession(products: any[], userId: number | null) {
    const cookieStore = await cookies()
    const jwt = cookieStore.get('jwt')?.value

    try {
        const headers: any = {}
        if (jwt) {
            headers['Authorization'] = `Bearer ${jwt}`
        }

        console.log("--- DEBUG CHECKOUT ---");
        console.log("URL:", `${STRAPI_URL}/api/orders`);
        console.log("JWT Present:", !!jwt);
        console.log("User ID:", userId);

        // Strip down the product data to minimum required for backend and order history
        const lineItems = products.map((product) => ({
            id: product.id,
            documentId: product.documentId,
            product_name: product.product_name,
            slug: product.slug,
            price: product.price,
            brand_name: product.brand?.brand_name,
            image: product.images?.[0]?.url,
            quantity: 1
        }));

        const { data } = await axios.post(`${STRAPI_URL}/api/orders`, {
            products: lineItems,
            user: userId
        }, {
            headers
        })

        return { stripeSession: data.stripeSession }
    } catch (error: any) {
        console.error('Checkout error:', error.response?.data?.error || error.message)
        return { error: error.response?.data?.error?.message || 'Failed to create checkout session.' }
    }
}

import { CategoryType } from "@/types/category";
import { useEffect, useState } from "react";

export function useGetCategory() {
    const url = `${process.env.NEXT_PUBLIC_BACKEND_URL}/api/categories?populate=*`;
    const [result, setResult] = useState<CategoryType[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        (async () => {
            try {
                const response = await fetch(url);
                const json = await response.json();
                setResult(json.data);
                setIsLoading(false);
            } catch (error: any) {
                setError(error);
                setIsLoading(false);
            } finally {
                setIsLoading(false);
            }
        })()

    }, [url])

    return { isLoading, result, error }
}

